mapper.select:
SELECT 
FROM storeRating
WHERE lan_code = #{lanCode} AND id = #{id}

mapper.update:
UPDATE storeRating
SET taste = #{taste},
WHERE rat_num = #{ratNum} AND email = #{email} AND lan_code = #{lanCode} AND id = #{id}

mapper.delete:
DELETE
FROM storeRating
WHERE rat_num = #{ratNum} AND email = #{email} AND lan_code = #{lanCode} AND id = #{id}
